function MakeBootstrapGridOverlay(options){if(!("showByDefault" in options)){options.showByDefault=true;}if(!("color" in options)){options.color="#5086b8";}if(!("opacity" in options)){options.opacity="0.3";}if(!("showOuterGuides" in options)){options.showOuterGuides=true;}if(!("outerGuidesColor" in options)){options.outerGuidesColor="#27506d";}if(!("showInnerGuides" in options)){options.showInnerGuides=true;}if(!("innerGuidesColor" in options)){options.innerGuidesColor="#000000";}if(!("showHorizontalLines" in options)){options.showHorizontalLines=false;}if(!("horizontalLinesColor" in options)){options.horizontalLinesColor="#000000";}if(!("horizontalLinesOpacity" in options)){options.horizontalLinesOpacity="0.1";}if(!("horizontalLinesHeight" in options)){options.horizontalLinesHeight="25";}if(!("hotkey" in options)){options.hotkey="G";}$("body").append('<div id="justix-bootstrap-grid" style="position:fixed;width:100%;height:100%;left:0;top:0;z-index:9999;"><div class="container" style="height:100%;"><div id="justix-bootstrap-grid-row" class="row" style="height:100%;"></div></div></div>');if(options.showByDefault===false){$("div#justix-bootstrap-grid").hide();}for(var i=1;i<=12;i++){$("div#justix-bootstrap-grid-row").append('<div id="justix-bootstrap-grid-column" class="col-xs-1" style="height:100%;"><div id="justix-bootstrap-grid-column-inner" style="background:'+options.color+";opacity:"+options.opacity+';height:100%;"></div></div>');}if(options.showOuterGuides===true){$("div#justix-bootstrap-grid-column").css({"border-right":"1px solid "+options.outerGuidesColor});$("div#justix-bootstrap-grid-column:first").css({"border-left":"1px solid "+options.outerGuidesColor});}if(options.showInnerGuides===true){$("div#justix-bootstrap-grid-column-inner").css({"border-right":"1px solid "+options.innerGuidesColor,"border-left":"1px solid "+options.innerGuidesColor});}if(options.showHorizontalLines===true){function DrawHorizontalLines(){var overlay_height=$("div#justix-bootstrap-grid").height();var lines_count=parseInt(overlay_height/options.horizontalLinesHeight);$("div#justix-bootstrap-grid").append('<div id="justix-bootstrap-grid-lines" style="width:100%;height:100%;position:fixed;top:0;left:0;"></div>');for(var i=1;i<=lines_count;i++){$("div#justix-bootstrap-grid-lines").append('<div style="width:100%;height:'+options.horizontalLinesHeight+"px;border-bottom:1px solid "+options.horizontalLinesColor+";opacity:"+options.horizontalLinesOpacity+';"></div>');}}DrawHorizontalLines();$(window).resize(function(){$("div#justix-bootstrap-grid-lines").remove();DrawHorizontalLines();});}document.onkeydown=function(e){if(e.keyCode==options.hotkey.charCodeAt(0)){$("div#justix-bootstrap-grid").toggle();}};}